$("#contactForm").validator().on("submit", function (event) {
    if (event.isDefaultPrevented()) {
        // handle the invalid form...
        formError();
        submitMSG(false, "入力内容をご確認ください。");
    } else {
        // everything looks good!
        event.preventDefault();
        submitForm();
    }
});

function submitForm(){
    // Initiate Variables With Form Content
    var name = $("#name").val();
    var from = $("#from").val();
    var subject = $("#subject").val();
    var content = $("#content").val();

    var sendMail = firebase.functions().httpsCallable('sendMail');    
    sendMail({ name: name, from: from, subject: subject, content: content })
    .then(function(result){
        formSuccess();
    })
    .catch(function(error){
        formError();
    });
}

function formSuccess(){
    $("#contactForm")[0].reset();
    submitMSG(true, "送信しました。")
}

function formError(){
    $("#contactForm").removeClass().addClass('shake animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function(){
        $(this).removeClass();
    });
}

function submitMSG(valid, msg){
    if(valid){
        var msgClasses = "h3 text-left tada text-success";
    } else {
        var msgClasses = "h3 text-left text-danger";
    }
    $("#msgSubmit").removeClass().addClass(msgClasses).text(msg);
}